import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState();

  //useEffect cant be async function (not allowed) so it is nested inside it to mask it
  //thus overall useEffect function does not return a promise
  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        "https://react-http-74e3d-default-rtdb.firebaseio.com/meals.json"
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();

      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      setMeals(loadedMeals);
      setIsLoading(false);
    };

    try {
      fetchMeals();
    } catch (error) {
      setIsLoading(false);
      setIsError(error.message);
    }
  }, []);

  if (isLoading) {
    return (
      <section className={classes.mealIsLoading}>
        <p>loading..</p>
      </section>
    );
  }

  if (isError) {
    return (
      <section className={classes.mealIsLoading}>
        <p>{isError}</p>
      </section>
    );
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
